package com.unascribed.sup;

import java.io.ByteArrayInputStream;
import java.security.cert.CertificateException;
import java.security.cert.CertificateFactory;
import java.security.cert.X509Certificate;
import java.util.Base64;

class CACerts {

	public static final X509Certificate
		ISRG_ROOT_X1 = cert(""),
		USERTRUST_ECC = cert(""),
		AMAZON_ROOT_CA_1 = cert(""),
		GTS_ROOT_R4 = cert("")
	;
	
	private static X509Certificate cert(String str) {
		try {
			return (X509Certificate)CertificateFactory.getInstance("X.509").generateCertificate(new ByteArrayInputStream(Base64.getDecoder().decode(str)));
		} catch (CertificateException e) {
			throw new AssertionError(e);
		}
	}

}
